import React, { useState } from "react";
import axios from "axios";

const CarSearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResult, setSearchResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:3001/api/cars/${searchTerm}`
      );
      setSearchResult(response.data.car); // Set the search result to the car object
      setError(null); // Clear any previous errors
      console.log(response.data.car);
    } catch (error) {
      setSearchResult(null); // Clear the search result
      setError("Car not found"); // Set the error message
      console.error("Error searching for car:", error);
    }
  };

  return (
    <div>
      <h2>Search Cars by MongoDB ID</h2>
      <input
        type="text"
        placeholder="Enter MongoDB ID"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {error && <p>{error}</p>}{" "}
      {/* Display error message if there's an error */}
      {
        searchResult &&
          {
            /* Display car info if search result is available */
          }
        // <div>
        //   <p>Car Model: {searchResult.carModel}</p>
        //   {/* Display other car details as needed */}
        // </div>
      }
    </div>
  );
};

export default CarSearchPage;
